import numpy as np
import lda
import lda.datasets
from nltk.tokenize import RegexpTokenizer
from nltk.corpus import stopwords
import os


def getWords():
    stopset = set(stopwords.words('english'))
    output_path = '/Users/weiwang/Desktop/dataset'
    words = []

    for dir_entry in os.listdir(output_path):
        dir_entry_path = os.path.join(output_path, dir_entry)
        if os.path.isfile(dir_entry_path):
            with open(dir_entry_path, 'r') as text_file:
                text = text_file.read()
                tokenizer = RegexpTokenizer(r'\w+')
                tokens = tokenizer.tokenize(str(text))
                for w in tokens:
                    if w not in stopset:
                        if w not in words:
                            words.append(w)
    return words


def getList(words, a):
    b = []
    for i, item in enumerate(words):
        if item in a:
            b.append(i)
    return b


def calculate(i, topic_word_, doc_topic_, b):
    c = 0
    for j in b:
        total = 0
        for k in range(5):
            total = total + doc_topic_[i][k] * topic_word_[k][j]
        c = c + total

    return c


