def getService(a, topic_word_, doc_topic_):
    num = 11
    words = getWords()
    b = getList(words, a)
    serviceId = -1
    value = -1
    for i in range(num):
        c = calculate(i, topic_word_, doc_topic_, b)
        if c >= value:
            serviceId = i
    return serviceId